import { useMutation } from '@apollo/react-hooks';
import gql from "graphql-tag"
import { Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const LOGIN_USUARIO = gql`
mutation($correo: String!, $password: String!){
  login(correo: $correo, password: $password) {
    token
  }
}
`

const Login = ()=>{

    const [loginUser,{ data, loading, error }] = useMutation(LOGIN_USUARIO)
  
    if (loading) return 'Submitting...';
    if (error) return `Submission error! ${error.message}`;
   
  
  
    let correo,password
    
  
    return(
      <div className="Container">
        <div style={{
            width:"30%"}}>
        <form onSubmit={async e=>{
          e.preventDefault()      
          loginUser({variables:{correo: correo.value,password:password.value}})
          console.log(data)
  
        }} >
           
            <h2>Login</h2>
            <div className= "form-group">
              <label htmlFor= "correo">Correo: </label>
              <input ref={node =>{correo=node}} id="correo" className="form-control"></input>
            </div>
            <div className= "form-group">
              <label htmlFor= "password">Password: </label>
              <input ref={node =>{password=node} } type="password" id="password" className="form-control"></input>
            </div>
           
            <div className= "form-group">
              <Button type="submit" className="btn btn-primary">Ingresar</Button>
            </div>
          
  
        </form>
        </div>
      </div>
  
    )
  }

  export default Login
