import { useMutation } from '@apollo/react-hooks';
import gql from "graphql-tag"
import { Button, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const LOGIN_USUARIO = gql`
mutation($correo: String!, $password: String!){
  login(correo: $correo, password: $password) {
    token
  }
}
`

const Login = ()=>{

    const [loginUser,{ data, loading, error }] = useMutation(LOGIN_USUARIO)
  
    if (loading) return 'Submitting...';
    if (error) return `Submission error! ${error.message}`;
   
  
  
    let correo,password
    
  
    return(
      <div className="container d-flex justify-content-center align-items-center" style={{height:"100vh"}}>

        <div style={{
            width:"30%"}} className="border p-4">
        <Form onSubmit={async e=>{
          e.preventDefault()      
          loginUser({variables:{correo: correo.value,password:password.value}})
          console.log(data)
  
        }} >
           
            <h2>Login</h2>
            <div className= "form-group">
              <label htmlFor= "correo">Correo: </label>
              <input ref={node =>{correo=node}} id="correo" className="form-control"></input>
            </div>

            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control type="email" placeholder="Enter email" />
                <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
        </Form.Text>
      </Form.Group>

            <div className= "form-group">
              <label htmlFor= "password">Password: </label>
              <input ref={node =>{password=node} } type="password" id="password" className="form-control"></input>
            </div>
           
            <div className= "form-group">
              <Button type="submit" className="btn btn-primary">Ingresar</Button>
            </div>
          
  
        </Form>
        </div>
      </div>
  
    )
  }

  export default Login
