import { useMutation } from '@apollo/react-hooks';
import gql from "graphql-tag"
import { Button, Form, Modal } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link, useNavigate } from 'react-router-dom';
import { StrictMode } from 'react';
import { useState, Container, Row, Col } from 'react';



const LOGIN_USUARIO = gql`
mutation($correo: String!, $password: String!){
  login(correo: $correo, password: $password) {
    success
    token
    errors {
      message
    }
  }
}
`

function MydModalWithGrid(props) {
    return (
      <Modal {...props} aria-labelledby="contained-modal-title-vcenter">
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Using Grid in Modal
          </Modal.Title>
        </Modal.Header>
        <Modal.Body className="show-grid">
          <Container>
            <Row>
              <Col xs={12} md={8}>
                .col-xs-12 .col-md-8
              </Col>
              <Col xs={6} md={4}>
                .col-xs-6 .col-md-4
              </Col>
            </Row>
  
            <Row>
              <Col xs={6} md={4}>
                .col-xs-6 .col-md-4
              </Col>
              <Col xs={6} md={4}>
                .col-xs-6 .col-md-4
              </Col>
              <Col xs={6} md={4}>
                .col-xs-6 .col-md-4
              </Col>
            </Row>
          </Container>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }

const Login = ()=>{

    const navigate = useNavigate() 

    const [loginUser,{ data, loading, error }] = useMutation(LOGIN_USUARIO)

    const [modalShow, setModalShow] = useState(false);

    

    if (loading) return 'Submitting...';
    if (error) return `Submission error! ${error.message}`;
   
  
   

    return(
        <StrictMode>
      <div className="container d-flex justify-content-center align-items-center" style={{height:"100vh"}}>

        <div style={{width:"30%"}} className="border p-4">
            <Form            
                    
                onSubmit={async e=>{
                    e.preventDefault()   
                    
                    
                    let respuesta = await loginUser({variables:{correo: e.target.correo.value,password:e.target.password.value}})
                    console.log(respuesta.data.login.success)
                    console.log("Correo: " , e.target.correo.value, " Contrase침a: " , e.target.password.value)
                    if (respuesta.data.login.success) {
                        navigate("/mainview")
                    }else {setModalShow(true)}
    
                }} >
            
                <legend>Login</legend>
            
                <Form.Group className="mb-3" controlId="correo">
                    <Form.Label>Direcci칩n de correo</Form.Label>
                    <Form.Control type="email" placeholder="ingrese su correo" />
                    <Form.Text className="text-muted">
                        Nunca se compartira su correo
                    </Form.Text>
                </Form.Group>

                

                <Form.Group className="mb-3" controlId="password">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Contrase침a" />
                </Form.Group>
        
                
                <Button variant="primary" type="submit">
                    Ingresar
                </Button>

                <Form.Group className="mt-3 d-flex justify-content-end" controlId="registrarse">
                <p>No eres miembro? <Link to="/agregar_usuario">Registrate</Link></p>
                </Form.Group>
                
                
            </Form>
        </div>
        <MydModalWithGrid show={modalShow} onHide={() => setModalShow(false)} />
      </div>
      </StrictMode>
    )
  }

  export default Login
