import { useMutation } from '@apollo/react-hooks';
import gql from "graphql-tag"
import { Button, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom';
import { useState } from 'react';


const LOGIN_USUARIO = gql`
mutation($correo: String!, $password: String!){
  login(correo: $correo, password: $password) {
    success
    token
    errors {
      message
    }
  }
}
`

const Login = ()=>{

    const [loginUser,{ data, loading, error }] = useMutation(LOGIN_USUARIO)

    

    if (loading) return 'Submitting...';
    if (error) return `Submission error! ${error.message}`;
   
  
    
    const [correo,setCorreo] = useState("")
    const [password,setPassword]= useState("")

    function handleChange(campo,valor){
        if (campo="correo"){setCorreo(valor)}
        if (campo="password"){setPassword(valor)}
    }

    return(
      <div className="container d-flex justify-content-center align-items-center" style={{height:"100vh"}}>

        <div style={{width:"30%"}} className="border p-4">
            <Form            
                    
                onSubmit={async e=>{
                    e.preventDefault()   
                    
                    
                    loginUser({variables:{correo: correo,password:password}})
                    console.log(data.login.success)
                    console.log("Correo: " , correo, " Contrase침a: ",password) //e.target.password.value)
            
    
                }} >
            
                <legend>Login</legend>
            
                <Form.Group className="mb-3" controlId="correo">
                    <Form.Label>Direcci칩n de correo</Form.Label>
                    <Form.Control type="email" placeholder="ingrese su correo" 
                        onChange={(e)=>handleChange("correo",e.target.value)}/>
                    <Form.Text className="text-muted">
                        Nunca se compartira su correo
                    </Form.Text>
                </Form.Group>

                

                <Form.Group className="mb-3" controlId="password">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Contrase침a" onChange={(e)=>handleChange("password",e.target.value)}/>
                </Form.Group>
        
                
                <Button variant="primary" type="submit">
                    Ingresar
                </Button>

                <Form.Group className="mt-3 d-flex justify-content-end" controlId="registrarse">
                <p>No eres miembro? <Link to="/agregar_usuario">Registrate</Link></p>
                </Form.Group>
                
                
            </Form>
        </div>
      </div>
  
    )
  }

  export default Login
